INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('62130342','CT-16253','Inscription (CT)','2017-07-18T22:00:00.000Z','Important','Le service médical rendu par BENEPALI est identique à celui d''ENBREL dans ses indications chez l''adulte et chez l''enfant ayant un poids égal ou supérieur à 62,5 kg à savoir : Important dans :<br>- le traitement de la polyarthrite rhumatoïde en cas de réponse inadéquate aux traitements de fond, y compris le méthotrexate et insuffisant chez les patients non précédemment traité par le méthotrexate.<br>- chez les patients adultes ayant un psoriasis en plaques chronique sévère en échec, c''est-à-dire non répondeurs, avec une contre-indication ou intolérants à au moins deux traitements systémiques parmi la photothérapie, le MTX et la ciclosporine et insuffisant pour les autres patients ne répondant pas à ces critères de mise sous traitement.<br>- dans le traitement du rhumatisme psoriasique, de la spondylarthrite ankylosante, de la spondylarthrite axiale non radiographique. <br>- dans la polyarthrite de l''enfant à partir de 2 ans et de l''adolescent ayant un poids égal ou supérieur à 62,5 kg en cas de réponse inadéquate ou d''intolérance avérée au méthotrexate, <br><br>Il n''y a pas lieu d''utiliser BENEPALI chez les enfants et les adolescents ayant un poids inférieur à 62,5 kg.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('62130342','CT-16253','Inscription (CT)','2017-07-18T22:00:00.000Z','Insuffisant','Le service médical rendu par BENEPALI est identique à celui d''ENBREL dans ses indications chez l''adulte et chez l''enfant ayant un poids égal ou supérieur à 62,5 kg à savoir insuffisant :<br>- dans le traitement de la polyarthrite rhumatoïde chez les patients non précédemment traité par le méthotrexate.<br>- chez les patients adultes ayant un psoriasis en plaques chronique sévère en échec pour les autres patients ne répondant pas aux ces critères de mise sous traitement.<br><br>Il n''y a pas lieu d''utiliser BENEPALI chez les enfants et les adolescents ayant un poids inférieur à 62,5 kg.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('62130342','CT-16253','Inscription (CT)','2017-07-18T22:00:00.000Z','Modéré','Le service médical rendu par BENEPALI est identique à celui d''ENBREL dans ses indications chez l''adulte et chez l''enfant ayant un poids égal ou supérieur à 62,5 kg à savoir : modéré dans :<br>- le traitement du psoriasis en plaques sévère chronique de l''enfant à partir de 6 ans et de l''adolescent ayant un poids égal ou supérieur à 62,5 kg en cas de contrôle inadéquat, ou d''intolérance aux autres traitements systémiques ou à la photothérapie.<br>- l''oligoarthrite extensive de l''enfant à partir de 2 ans et de l''adolescent ayant un poids égal ou supérieur à 62,5 kg en cas de réponse inadéquate ou d''intolérance avérée au méthotrexate, l''arthrite psoriasique de l''adolescent à partir de l''âge de 12 ans si le poids est égal ou supérieur à 62,5 kg en cas de réponse inadéquate ou d''intolérance avérée au méthotrexate et l''arthrite liée à l''enthésite de l''adolescent à partir de l''âge de 12 ans si le poids est égal ou supérieur à 62,5 kg en cas de réponse inadéquate ou d''intolérance avérée au traitement de référence.<br><br>Il n''y a pas lieu d''utiliser BENEPALI chez les enfants et les adolescents ayant un poids inférieur à 62,5 kg.');
