INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('69653644','CT-16253','Inscription (CT)','2017-07-18T22:00:00.000Z','Insuffisant','Le service médical rendu par BENEPALI est identique à celui d''ENBREL dans ses indications chez l''adulte et chez l''enfant ayant un poids égal ou supérieur à 62,5 kg à savoir insuffisant :<br>- dans le traitement de la polyarthrite rhumatoïde chez les patients non précédemment traité par le méthotrexate.<br>- chez les patients adultes ayant un psoriasis en plaques chronique sévère en échec pour les autres patients ne répondant pas aux ces critères de mise sous traitement.<br><br>Il n''y a pas lieu d''utiliser BENEPALI chez les enfants et les adolescents ayant un poids inférieur à 62,5 kg.');
