INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61881699','CT-15938','Inscription (CT)','2017-02-21T23:00:00.000Z','Important','" Le service médical rendu par SANDOSTATINE LP 10 mg, SANDOSTATINE LP 20 mg et SANDOSTATINE LP 30 mg, poudre et solvant pour suspension injectable est important dans le traitement :<br>- de l''acromégalie chez les patients pour lesquels la chirurgie est inadaptée ou inefficace, ou pendant la période transitoire précédant la complète efficacité de la radiothérapie .<br>- des patients avec des symptômes associés aux tumeurs endocrines gastro-entéro-pancréatiques fonctionnelles, par exemple les tumeurs carcinoïdes avec signe(s) clinique(s) d''un syndrome carcinoïde,<br>- des adénomes thyréotropes : <br>o lorsque la sécrétion n''est pas normalisée après chirurgie et/ou radiothérapie .<br>o chez les patients ne relevant pas d''un traitement chirurgical . <br>o chez les patients irradiés, en attente de l''efficacité de la radiothérapie.<br><br>Le service médical rendu par SANDOSTATINE LP 30 mg, poudre et solvant pour suspension injectable est important uniquement dans le traitement des patients atteints de tumeurs neuroendocrines avancées, non progressives et avec un index KI67 = 10%, ayant pour origine l''intestin moyen ou de localisation primitive inconnue lorsque les sites primitifs ne correspondant pas à l''intestin moyen ont été exclus. "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61881699','CT-15938','Inscription (CT)','2017-02-21T23:00:00.000Z','Insuffisant','Le service médical rendu par SANDOSTATINE LP 30 mg, poudre et solvant pour suspension injectable est insuffisant pour justifier d''une prise en charge par la solidarité nationale lorsque ces tumeurs sont progressives.');
