INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('64386835','CT-15792','Inscription (CT)','2017-01-10T23:00:00.000Z','Important','" Le service médical rendu par CIMZIA 200 mg, solution injectable en stylo pré-rempli AutoClicks, est important dans :<br> Le traitement de la polyarthrite rhumatoïde (PR) active, modérée à sévère, de l''adulte, en association au méthotrexate (MTX), lorsque la réponse aux traitements de fond (DMARDs), y compris le MTX, est inadéquate. <br> Le traitement de la spondyloarthrite axiale active sévère de l''adulte, incluant la spondylarthrite ankylosante et la spondyloarthrite axiale sans signes radiographiques de spondylarthrite ankylosante mais avec des signes objectifs d''inflammation à l''IRM et/ou un taux élevé de CRP, en cas de réponse inadéquate ou d''intolérance aux AINS.<br> Le traitement du rhumatisme psoriasique actif de l''adulte, en association au méthotrexate (MTX), lorsque la réponse aux traitements de fond (DMARDs) est inadéquate. "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('64386835','CT-15792','Inscription (CT)','2017-01-10T23:00:00.000Z','Insuffisant','Le service médical rendu par CIMZIA 200 mg, solution injectable en stylo pré-rempli AutoClicks est insuffisant pour justifier d''une prise en charge par la solidarité nationale dans le traitement de la polyarthrite rhumatoïde sévère, active et évolutive, en association au méthotrexate (MTX), chez les adultes non précédemment traités par le MTX ou les autres traitements de fond (DMARDs) ».');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66140857','CT-15789','Inscription (CT)','2017-03-21T23:00:00.000Z','Insuffisant','Le service médical rendu par ARIXTRA 1,5 mg reste insuffisant pour une prise en charge par la solidarité nationale dans ses indications en prévention des événements thrombo-emboliques veineux et dans l''indication « Traitement de la thrombose veineuse superficielle spontanée aiguë symptomatique des membres inférieurs de l''adulte, sans thrombose veineuse profonde associée ».');
