INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61350360','CT-15786','Renouvellement d''inscription (CT)','2017-01-10T23:00:00.000Z','Important','Le service médical rendu par cette spécialité reste important dans les indications de l''AMM.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61730170','CT-15782','Renouvellement d''inscription (CT)','2017-01-24T23:00:00.000Z','Important','" Le service médical rendu par PARACETAMOL CODEINE SANDOZ, comprimé, reste important dans l''indication de l''AMM à l''exception des populations suivantes :<br>- l''enfant de moins de 12 ans,<br>- la femme allaitante,<br>- les patients connus pour être métaboliseurs ultra rapides du CYP2D6,<br>- les patients de moins de 18 ans après amygdalectomie et/ou adénoïdectomie dans le cadre d''un syndrome d''apnée obstructive du sommeil. "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61730170','CT-15782','Renouvellement d''inscription (CT)','2017-01-24T23:00:00.000Z','Insuffisant','" Le service médical rendu par PARACETAMOL CODEINE SANDOZ, comprimé, devient insuffisant dans les populations suivantes :<br>- l''enfant de moins de 12 ans,<br>- la femme allaitante,<br>- les patients connus pour être métaboliseurs ultra rapides du CYP2D6,<br>- les patients de moins de 18 ans après amygdalectomie et/ou adénoïdectomie dans le cadre d''un syndrome d''apnée obstructive du sommeil. "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66585953','CT-15776','Inscription (CT)','2017-01-10T23:00:00.000Z','Faible','Chez l''adulte dans l''indication de l''AMM et sous réserve du respect des mesures de minimisation des risques cardio-vasculaires, la Commission considère que le service médical rendu par les spécialités à base de dompéridone est faible.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66585953','CT-15776','Inscription (CT)','2017-01-10T23:00:00.000Z','Insuffisant','Chez l''enfant dans l''indication de l''AMM, le service médical rendu par les spécialités à base de dompéridone est insuffisant pour une prise en charge par la solidarité nationale.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('68050732','CT-15772','Inscription (CT)','2017-01-24T23:00:00.000Z','Important','Le service médical rendu par POTASSIUM H2 PHARMA 25 mg/mL, sirop (flacon de 200 mL) est important dans l''indication de l''AMM.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66822683','CT-15770','Inscription (CT)','2016-11-29T23:00:00.000Z','Faible','Le service médical rendu par le zolpidem dans l''indication « troubles sévères du sommeil dans les cas suivants : insomnie occasionnelle, insomnie transitoire » est faible. <br>La prescription d''hypnotiques doit s''inscrire dans une stratégie à court terme en seconde intention si les règles d''hygiène du sommeil ne suffisent pas. Dès l''instauration du traitement, le médecin doit expliquer au patient la durée du traitement et ses modalités d''arrêt du fait des risques d''effets indésirables et de dépendance.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('67057492','CT-15765','Inscription (CT)','2016-11-29T23:00:00.000Z','Important','Le service médical rendu par LINEZOLIDE MYLAN PHARMA 600 mg, comprimé pelliculé est important dans les indications de l''AMM.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66532840','CT-15764','Réévaluation SMR et ASMR','2017-06-06T22:00:00.000Z','Important','Le service médical rendu par YERVOY en monothérapie dans le traitement du mélanome avancé est important en 2ème ligne et plus en l''absence de mutation B-RAF et en 3ème ligne et plus en présence de la mutation B-RAF.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66532840','CT-15764','Réévaluation SMR et ASMR','2017-06-06T22:00:00.000Z','Insuffisant','Le service médical rendu par YERVOY en monothérapie dans le traitement du mélanome avancé (non résécable ou métastatique) est insuffisant pour justifier d''une prise en charge par la solidarité nationale chez les patients naïfs de traitement (quel que soit le statut B-RAF de la tumeur). <br>Le service médical rendu par YERVOY en monothérapie dans le traitement du mélanome avancé est insuffisant pour justifier d''une prise en charge par la solidarité nationale en 2ème ligne en présence de la mutation B-RAF.');
