INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61490049','CT-15578','Renouvellement d''inscription (CT)','2017-01-24T23:00:00.000Z','Modéré','Le service médical rendu par HALDOL 1 mg comprimé et HALDOL 2 mg/ml solution buvable reste modéré dans le traitement symptomatique de courte durée de l''anxiété de l''adulte en cas d''échec des thérapeutiques habituelles.<br>Le service médical rendu par HALDOL 2 mg/ml solution buvable reste modéré dans la prise en charge des troubles graves du comportement de l''enfant.<br>La Commission de la transparence a tenu compte du caractère très limité des données cliniques disponibles dans cette indication et d''une place mal définie des antipsychotiques dans la prise en charge des troubles graves du comportement avec agitation et agressivité de l''enfant.');
