INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60762795','CT-15097','Renouvellement d''inscription (CT)','2016-09-06T22:00:00.000Z','Important','le service médical rendu par MORPHINE (CHLORHYDRATE) AGUETTANT reste :<br>-Important dans la prise en charge des douleurs intenses et/ou rebelles aux antalgiques de niveau plus faible, des douleurs sévères d''origine cancéreuse, des douleurs aiguës sévères non cancéreuses (douleurs post-opératoires), et des douleurs chroniques sévères d''origine neuropathiques.<br><br>-Important dans la prise en charge des douleurs intenses et/ou rebelles rencontrées dans l''arthrose du genou ou de la hanche et dans la lombalgie chronique, comme traitement de dernier recours, à un stade où les solutions chirurgicales sont envisagées et chez des patients non candidats (refus ou contre-indication) à une chirurgie de remplacement prothétique (coxarthrose ou gonarthrose), pour une durée la plus courte possible du fait du risque d''effet indésirable grave et de l''absence de données à long terme. La place de cette classe thérapeutique doit être la plus réduite possible, après échec des autres mesures médicamenteuses et du traitement physique recommandés dans ces indications.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60762795','CT-15097','Renouvellement d''inscription (CT)','2016-09-06T22:00:00.000Z','Insuffisant','le service médical rendu par MORPHINE (CHLORHYDRATE) AGUETTANT reste :<br>-insuffisant dans les douleurs intenses et/ou rebelles rencontrées dans toutes les autres situations de douleurs chroniques non cancéreuses et non neuropathiques, notamment dans les rhumatismes inflammatoires chroniques, représentés principalement par la polyarthrite rhumatoïde et la spondylarthrite.');
