INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61276045','CT-15036','Réévaluation SMR et ASMR','2016-05-10T22:00:00.000Z','Important','Le service médical rendu par HERCEPTIN, en association à une chimiothérapie à base de capécitabine ou 5-fluoro-uracile et de cisplatine, reste important dans le traitement de l''adénocarcinome métastatique de l''estomac ou de la jonction oesogastrique HER2 positif chez les patients n''ayant pas été précédemment traités pour leur maladie métastatique.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61276045','CT-15035','Réévaluation SMR et ASMR','2016-05-24T22:00:00.000Z','Important','Le service médical rendu par HERCEPTIN est important dans « le traitement de patients adultes atteints d''un cancer du sein précoce HER2 positif :<br>- après chirurgie, chimiothérapie (néoadjuvante ou adjuvante) et radiothérapie (si indiquée),<br>- après une chimiothérapie adjuvante avec la doxorubicine et le cyclophosphamide, en association avec le paclitaxel ou le docétaxel,<br>- en association à une chimiothérapie adjuvante associant le docétaxel et le carboplatine » <br>et dans « le traitement de patients adultes atteints d''un cancer du sein précoce HER2 positif en association à une chimiothérapie néoadjuvante, suivie d''un traitement adjuvant avec HERCEPTIN, chez les patients ayant une maladie localement avancée (y compris inflammatoire) ou des tumeurs mesurant plus de 2 cm de diamètre »');
