INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60981789','CT-14965','Modification des conditions d''inscription (CT)','2016-04-19T22:00:00.000Z','Important','Le service médical rendu par EZETROL est important dans les indications :<br>- « EZETROL en association avec une statine (inhibiteur de l''HMG CoA réductase) est indiqué comme traitement adjuvant au régime chez les patients ayant une hypercholestérolémie primaire (familiale hétérozygote et non familiale) et qui ne sont pas contrôlés de façon appropriée par une statine seule.<br>EZETROL en monothérapie est indiqué comme traitement adjuvant au régime chez les patients ayant une hypercholestérolémie primaire (familiale hétérozygote et non familiale) pour lesquels un traitement par statine est inapproprié ou est mal toléré. »<br>- « EZETROL en association avec une statine est indiqué comme traitement adjuvant au régime chez les patients ayant une HFHo. Ces patients peuvent recevoir également des traitements adjuvants (exemple : aphérèse des LDL). »<br>- « EZETROL est indiqué comme traitement adjuvant au régime, chez les patients ayant une sitostérolémie familiale homozygote. »');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('64565845','CT-14964','Renouvellement d''inscription (CT)','2016-03-01T23:00:00.000Z','Important','Le service médical rendu par ALDACTONE reste important dans :<br>- le traitement de l''hyperaldostéronisme primaire et de l''hyperaldostéronisme réactionnel à un traitement diurétique efficace,<br>- le traitement de l''hypertension artérielle essentielle,<br>- le traitement des états oedémateux pouvant s''accompagner d''un hyperaldostéronisme secondaire (oedème et ascite de l''insuffisance cardiaque, syndrome néphrotique, ascite cirrhotique).<br>Le service médical rendu par ALDACTONE 25 mg reste important dans le traitement de l''insuffisance cardiaque stade III ou IV selon la classification de la NYHA (fraction d''éjection systolique inférieure ou égale à 35%), en association avec un traitement comprenant un diurétique de l''anse, un inhibiteur de l''enzyme de conversion, et un digitalique dans la majorité des cas.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('64565845','CT-14964','Renouvellement d''inscription (CT)','2016-03-01T23:00:00.000Z','Insuffisant','Le service médical rendu par ALDACTONE reste insuffisant pour justifier d''une prise en charge par la solidarité nationale dans le traitement adjuvant de la myasthénie.');
