INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60131132','CT-14717','Inscription (CT)','2015-09-15T22:00:00.000Z','Faible','Le service médical rendu par XELEVIA 100 mg est faible en bithérapie orale en association à un sulfamide hypoglycémiant.<br>Le service médical rendu par XELEVIA 50 mg est faible :<br>o en monothérapie .<br>o en bithérapie orale, en association à un sulfamide hypoglycémiant.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60131132','CT-14717','Inscription (CT)','2015-09-15T22:00:00.000Z','Important','Le service médical rendu par XELEVIA 100 mg est important :<br>o en bithérapie orale en association à la metformine .<br>o en trithérapie orale en association à la metformine et à un sulfamide hypoglycémiant .<br>o en association avec l''insuline et la metformine.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60131132','CT-14717','Inscription (CT)','2015-09-15T22:00:00.000Z','Insuffisant','Le service médical rendu par XELEVIA 100 mg est insuffisant :<br>o en monothérapie, chez les patients insuffisamment contrôlés par le régime alimentaire et l''exercice physique seuls et pour lesquels la metformine est contre-indiquée ou n''est pas tolérée .<br>o en bithérapie en association à l''insuline.<br>Le service médical rendu par XELEVIA 50 mg est insuffisant :<br>o en bithérapie avec l''insuline .<br>o en bithérapie orale, en association à la metformine, lorsque celle-ci, utilisée en monothérapie avec régime alimentaire et exercice physique, ne permet pas d''obtenir un contrôle adéquat de la glycémie .<br>o en trithérapie orale, en association à un sulfamide hypoglycémiant et à la metformine, lorsqu''une bithérapie avec ces deux médicaments avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie .<br>o en association à l''insuline avec la metformine lorsqu''une dose stable d''insuline avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie.');
