INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61666369','CT-14555','Inscription (CT)','2015-12-01T23:00:00.000Z','Modéré','" Le service médical rendu par JINARC (tolvaptan) est modéré, uniquement chez les patients adultes atteints de PKRAD authentifiée (critère de Ravine modifiés par Pei, histoire familiale, ou test génétique) et progresseurs c''est-à-dire avec :<br>- un DFG > 30 ml/min/1,73 m2 <br>et<br>- une néphromégalie importante associée à un risque de perte de fonction rénale c''est-à-dire :<br>- un volume rénal ajusté à la taille > 600 ml/m à l''IRM . = 630 ml/m à l''échographie,<br>ou<br>- une longueur des reins > 16,7 cm à l''IRM . > 16,8 cm à l''échographie.<br>et<br>- des signes d''évolution rapide de la maladie tels que :<br>- la présence de manifestations cliniques (douleurs rénales, ou hémorragie ou infection intra-kystique, hématurie macroscopique) <br>ou <br>- une perte significative du DFG d''au moins 5 ml/min/an (appréciée par les formules du MDRD, CKD-EPI ou par la clairance de la créatinine). "');
