INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60595727','CT-14536','Inscription (CT)','2015-09-15T22:00:00.000Z','Insuffisant','£Le service médical rendu par JANUVIA 50 mg est insuffisant :<br>- en bithérapie avec l''insuline .<br>- en bithérapie orale, en association à la metformine, lorsque celle-ci, utilisée en monothérapie avec régime alimentaire et exercice physique, ne permet pas d''obtenir un contrôle adéquat de la glycémie .<br>- en trithérapie orale, en association à un sulfamide hypoglycémiant et à la metformine, lorsqu''une bithérapie avec ces deux médicaments avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie .<br>- en association à l''insuline avec la metformine lorsqu''une dose stable d''insuline avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie.');
