INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('65643325','CT-14429','Extension d''indication','2016-06-28T22:00:00.000Z','Important','Le service médical rendu par BUSILVEX, suivi par du cyclophosphamide (BuCy2), est important dans le traitement de conditionnement préalable à une greffe conventionnelle de cellules souches hématopoïétiques (CSH) chez l''adulte, lorsque cette association est considérée comme la meilleure option possible.<br>Le service médical rendu par BUSILVEX, suivi par du cyclophosphamide (BuCy4) ou du melphalan (BuMel), est important dans le traitement de conditionnement préalable à une greffe conventionnelle de cellules souches hématopoïétiques chez le nouveau-né, l''enfant et l''adolescent.<br>Le service médical rendu par BUSILVEX, administré à la suite de fludarabine (FB), est important dans le traitement de conditionnement préalable à une greffe de cellules souches hématopoïétiques (CSH) chez l''adulte éligible à un conditionnement à intensité réduite (RIC).');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('69548508','CT-14428','Extension d''indication','2015-10-06T22:00:00.000Z','Important','Le service médical rendu par STIVARGA 40 mg est important dans l''extension d''indication « traitement des patients adultes atteints de tumeurs stromales gastro-intestinales (GIST) non résécables ou métastatiques ayant progressé lors d''un traitement antérieur par imatinib et sunitinib ou en cas d''intolérance à ces traitements ».');
