INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('63591602','CT-14402','Renouvellement d''inscription (CT)','2016-06-21T22:00:00.000Z','Important','" Le service médical rendu par FOSAVANCE reste important chez les patientes à risque d''insuffisance en vitamine D pour réduire le risque de fractures vertébrales et de hanche : <br>- chez les patientes ayant fait une fracture par fragilité osseuse, <br>- en l''absence de fracture, chez les femmes ayant une diminution importante de la densité osseuse (T score < -3) ou ayant un T score = -2,5 associé à d''autres facteurs de risque de fracture en particulier, un âge > 60 ans, une corticothérapie systémique ancienne ou actuelle à une posologie = 7,5 mg/jour d''équivalent prednisone, un indice de masse corporelle < 19 kg/m², un antécédent de fracture de l''extrémité du col du fémur chez un parent du premier degré (mère), une ménopause précoce (avant l''âge de 40 ans). "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('68300050','CT-14402','Renouvellement d''inscription (CT)','2016-06-21T22:00:00.000Z','Important','" Le service médical rendu par FOSAVANCE reste important chez les patientes à risque d''insuffisance en vitamine D pour réduire le risque de fractures vertébrales et de hanche : <br>- chez les patientes ayant fait une fracture par fragilité osseuse, <br>- en l''absence de fracture, chez les femmes ayant une diminution importante de la densité osseuse (T score < -3) ou ayant un T score = -2,5 associé à d''autres facteurs de risque de fracture en particulier, un âge > 60 ans, une corticothérapie systémique ancienne ou actuelle à une posologie = 7,5 mg/jour d''équivalent prednisone, un indice de masse corporelle < 19 kg/m², un antécédent de fracture de l''extrémité du col du fémur chez un parent du premier degré (mère), une ménopause précoce (avant l''âge de 40 ans). "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('66661714','CT-14401','Renouvellement d''inscription (CT)','2015-06-21T22:00:00.000Z','Important','Le service médical rendu par CIMZIA, en association au méthotrexate, reste important dans : <br>- le traitement de la polyarthrite rhumatoïde active, modérée à sévère, de l''adulte, lorsque la réponse aux traitements de fond (DMARDs), y compris le méthotrexate, est inadéquate,<br>- le traitement de la spondyloarthrite axiale active sévère incluant la spondylarthrite ankylosante et la spondyloarthrite axiale sans signes radiologiques de spondylarthrite ankylosante, mais avec des signes objectifs d''inflammation à l''IRM et/ou un taux élevé de CRP chez les adultes ayant eu une réponse insuffisante ou une intolérance aux anti-inflammatoires non stéroïdiens,<br>- le traitement du rhumatisme psoriasique actif de l''adulte, lorsque la réponse aux traitements de fond (DMARDs) est inadéquate.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61952274','CT-14400','Nouvel examen suite au dépôt de nouvelles données','2015-10-06T22:00:00.000Z','Important','Le service médical rendu par DUOPLAVIN est important dans l''indication « DuoPlavin est indiqué en prévention des événements liés à l''athérothrombose chez l''adulte déjà traité par le clopidogrel et l''acide acétylsalicylique (AAS). DuoPlavin est une association fixe pour la poursuite du traitement d''un : <br> Syndrome coronaire aigu sans sus-décalage du segment ST (angor instable ou infarctus du myocarde sans onde Q), y compris chez les patients bénéficiant d''une angioplastie coronaire avec pose de stent, <br> Infarctus du myocarde aigu avec sus-décalage du segment ST chez les patients traités médicalement et éligibles à un traitement thrombolytique ».');
