INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61480293','CT-14087','Renouvellement d''inscription (CT)','2016-04-19T22:00:00.000Z','Important','" Le service médical rendu par les spécialités EPREX reste important :<br>- dans le traitement de l''anémie associée à l''insuffisance rénale chronique : <br> chez les adultes et les enfants âgés de 1 à 18 ans hémodialysés et chez les patients adultes en dialyse péritonéale,<br> chez les adultes insuffisants rénaux non encore dialysés pour le traitement de l''anémie sévère d''origine rénale accompagnée de symptômes cliniques chez les patients.<br>- chez les adultes participant à un programme de transfusions autologues différées pour augmenter les dons de sang autologue : le traitement doit être uniquement administré aux patients présentant une anémie modérée (concentration d''hémoglobine comprise entre  10 et 13 g/dl [soit 6,2 - 8,1 mmol/l], sans carence martiale) s''il n''existe pas ou peu de méthodes d''épargne du sang lorsqu''une intervention chirurgicale majeure programmée nécessite de grandes quantités de sang (4 unités de sang ou plus chez les femmes et 5 unités de sang ou plus chez les hommes) .<br>- chez les adultes, sans carence martiale, devant avoir une intervention chirurgicale orthopédique majeure programmée, ayant un risque présumé important de complications transfusionnelles pour réduire l''exposition aux transfusions de sang homologue : l''utilisation devra être réservée aux patients ayant une anémie modérée (par exemple, concentration d''hémoglobine comprise entre 10 et 13 g/dl) qui n''ont pas accès à un programme de transfusions autologues différées et chez lesquels on s''attend à des pertes de sang modérées (900 à 1800 ml). "');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61480293','CT-14087','Renouvellement d''inscription (CT)','2016-04-19T22:00:00.000Z','Modéré','Le service médical rendu par les spécialités EPREX reste modéré chez les adultes traités par chimiothérapie pour des tumeurs solides, un lymphome malin ou un myélome multiple, et à risque de transfusion en raison de leur état général (par exemple, état cardiovasculaire, anémie pré existante au début de la chimiothérapie) pour le traitement de l''anémie et la réduction des besoins transfusionnels.');
