INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('63209020','CT-13604','Renouvellement d''inscription (CT)','2014-07-08T22:00:00.000Z','Faible','Le service médical rendu par TAMIFLU reste faible dans le traitement prophylactique de la grippe dans les populations à risque de complication : enfant à partir de 1 an avec comorbidité, adulte jusqu''à 64 ans avec comorbidité et adulte à partir de 65 ans. Le service médical rendu dans le traitement curatif en période de pandémie reste faible chez l''enfant de moins de 12 mois. <br>En traitement curatif de la grippe, le service médical rendu par TAMIFLU, lorsqu''il est débuté dans un délai de 48h après l''apparition des symptômes, en période d''épidémie ordinaire de grippe ou en période de pandémie, reste faible chez les sujets ayant une forme clinique de grippe jugée sévère par le médecin ou compliquée d''emblée, les sujets ayant des facteurs de risque particuliers, dont la femme enceinte.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('63209020','CT-13604','Renouvellement d''inscription (CT)','2014-07-08T22:00:00.000Z','Insuffisant','Le service médical rendu par TAMIFLU reste insuffisant dans le traitement prophylactique de la grippe chez l''enfant et l''adulte sans comorbidités, ainsi qu''en en période de pandémie en traitement prophylactique chez l''enfant de moins de 12 mois. <br>En traitement curatif de la grippe, le service médical rendu par TAMIFLU reste insuffisant dans les autres situations.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('63209020','CT-13604','Renouvellement d''inscription (CT)','2014-07-08T22:00:00.000Z','Modéré','Le service médical rendu par TAMIFLU reste modéré dans le traitement prophylactique de la grippe chez les sujets à risque, dans les cas particuliers suivants : sujets vivant en collectivités (patients institutionnalisés), sujets présentant une contre-indication au vaccin, sujets immunodéprimés (notamment sujets ayant un SIDA, greffés, sujets traités par immunosuppresseurs), situations où la protection vaccinale est incomplète par rapport à la souche circulante.');
