INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('62815549','CT-13335','Renouvellement d''inscription (CT)','2014-11-18T23:00:00.000Z','Modéré','Dans le traitement prophylactique de la grippe, le service médical rendu par RELENZA reste modéré chez les sujets à risque, dans les cas particuliers suivants, sujets vivant en collectivités (patients institutionnalisés), sujets présentant une contre-indication au vaccin, sujets immunodéprimés (notamment sujets ayant un SIDA, greffés, sujets traités par immunosuppresseurs), situations où la protection vaccinale est incomplète par rapport à la souche circulante.');
