INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('65602428','CT-12572','Renouvellement d''inscription (CT)','2015-06-16T22:00:00.000Z','Faible','En conséquence, la Commission considère que le service médical rendu par XELEVIA 100 mg reste faible en bithérapie orale en association à un sulfamide hypoglycémiant. <br><br>Le service médical rendu par XELEVIA 50 mg :<br>- Reste faible en monothérapie.<br>- Est faible en bithérapie orale, en association à un sulfamide hypoglycémiant.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('65602428','CT-12572','Renouvellement d''inscription (CT)','2015-06-16T22:00:00.000Z','Important','Le service médical rendu par XELEVIA 100 mg reste important dans les 3 indications de l''AMM suivantes :<br>1) En bithérapie orale en association à la metformine.<br>2) En trithérapie orale en association à la metformine et à un sulfamide hypoglycémiant.<br>3) En association avec l''insuline et la metformine.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('65602428','CT-12572','Renouvellement d''inscription (CT)','2015-06-16T22:00:00.000Z','Insuffisant','Le service médical rendu par XELEVIA 100 mg reste insuffisant en bithérapie en monothérapie et en association à l''insuline.<br><br>Le service médical rendu par XELEVIA 50 mg est insuffisant en bithérapie avec l''insuline. <br>Le service médical rendu par XELEVIA 50 mg reste insuffisant dans les indications :<br>4) en bithérapie orale, en association à la metformine, lorsque celle-ci, utilisée en monothérapie avec régime alimentaire et exercice physique, ne permet pas d''obtenir un contrôle adéquat de la glycémie.<br>5) en trithérapie orale, en association à un sulfamide hypoglycémiant et à la metformine, lorsqu''une bithérapie avec ces deux médicaments avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie.<br>6) en association à l''insuline avec la metformine lorsqu''une dose stable d''insuline avec régime alimentaire et exercice physique ne permet pas d''obtenir un contrôle adéquat de la glycémie.');
