INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61177182','CT-11635','Renouvellement d''inscription (CT)','2011-10-18T22:00:00.000Z','Faible','Le service médical rendu reste faible dans les indications : traitement symptomatique du reflux gastro-oesophagien lorsque les règles hygiéno-diététiques ne se sont pas montrées efficaces (AZANTAC 75 mg) . ulcère gastrique ou duodénal évolutif (par AZANTAC 150 mg, 300 mg et 50 mg/2 ml) . oesophagite par reflux gastro-oesophagien (par AZANTAC 150 mg et 300 mg) . hémorragies de la maladie ulcéreuse gastro-duodénale (AZANTAC 50 mg/2 ml) . lésions gastro-duodénales liées au stress (AZANTAC 50 mg/2 ml) . en association à une bithérapie antibiotique, éradication de Helicobacter pylori en cas de maladie ulcéreuse gastro-duodénale (après preuve endoscopique de la lésion et de l''infection) (AZANTAC 300 mg).');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('61177182','CT-11635','Renouvellement d''inscription (CT)','2011-10-18T22:00:00.000Z','Insuffisant','Le service médical rendu par les spécialités AZANTAC 150 mg, 300 mg et 50mg/2ml reste insuffisant dans l''indication syndrome de Zollinger-Ellison. Pour la spécialité AZANTAC 150 mg, conformément aux avis de la même classe, le service médical rendu est insuffisant dans l''indication traitement d''entretien de l''ulcère duodénal.');
