INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('60424555','CT-8472','Renouvellement d''inscription (CT)','2010-10-05T22:00:00.000Z','Insuffisant','Le service médical rendu reste insuffisant dans les indications suivantes :  -Sinusites aiguës :  Etant donné l''activité modeste sur H. influenzae et le pourcentage élevé de souches de pneumocoques résistants, les macrolides ne sont plus adaptés au traitement des sinusites aiguës purulentes (cf. Recommandations Afssaps, octobre 2005)1.  -Surinfections des bronchites aiguës :   Au cours des bronchites aiguës, il n''y a pas d''indication d''une antibiothérapie1.');
INSERT INTO CIS_HAS_SMR_bdpm (cis,codeHAS,motifEval,dateAvisCT,valeurSMR,libelleSMR) VALUES ('64674645','CT-8468','Inscription (CT)','2010-11-30T23:00:00.000Z','Modéré','Compte tenu des faiblesses méthodologiques de l''étude présentée (Cf chapitre efficacité) mais considérant d''une part l''absence d''alternative médicamenteuse et d''autre part l''effet observé par rapport à la prise en charge actuelle, la Commission de la transparence attribue un service médical rendu modéré à l''association CEPLENE + IL2.');
